import { connect } from 'react-redux'
import {
    LoadOnePostDetail,
    UploadComment,
    UpdateComment,
    ToggleMoreComment
  } from '../store/actions/PostDetailActions'
import {LoadPosts} from '../store/actions/PostActions' 
import { useEffect } from 'react'
//import Post from '../components/Post'
import { useParams } from 'react-router-dom'


const mapStateToProps = ({ postDetailState, postState }) => {
    return { postDetailState, postState }
}

const mapDispatchToProps = (dispatch) => {
    return {
        fetchOnePostDetail: (id) => dispatch(LoadOnePostDetail(id)),
        updatePostState: (id) => dispatch(LoadPosts(id)),
        uploadComment: (id, newComment) => dispatch(UploadComment(id, newComment)),
        updateComment: (comment) => dispatch(UpdateComment(comment)),
        toggleMoreComment: (value) => dispatch(ToggleMoreComment(value))
    }
}

const PostDetail = (props) => {



    const {post_id} = useParams()

    useEffect(() => {
        const GetPost = async () => {
            //console.log("HELLO")
            let post = await props.fetchOnePostDetail(post_id)
            console.log(post, "USEFFECT")
            props.updatePostState(post)
        }
        GetPost()
    }, [post_id])
    //console.log(props.postDetailState, "POST DETAIL STATE")


    //console.log(props.postState, "POST STATE")

    return(
        <div className='post-detail' >
            {/* {props.postState.posts.map((post) => (
                <Post post={post} />
            ))} */}
            <h3> {props.postState.post.title}</h3>
            <h3> {props.postState.post.recipeUrl}</h3>
        </div>
    )
}

export default connect(mapStateToProps, mapDispatchToProps)(PostDetail)
