import React, {useState, useEffect} from 'react'
import { connect } from 'react-redux'
import {LoadPostDetail, UploadPost, UpdatePost} from '../store/actions/PostActions'
import { useParams } from 'react-router-dom'
import ReactStars from 'react-stars'
import AddPost from '../components/AddPost'


const mapStateToProps = ({ postState }) => {
    return { postState }
  }

const mapDispatchToProps = (dispatch) => {
    return {
      fetchPostDetail: (id) => dispatch(LoadPostDetail(id)),
      UploadPost: (id, post) => dispatch(UploadPost(id, post)),
      UpdatePost: (post) => dispatch(UpdatePost(post))
    }
}

const CreatePost = (props) => {
    const { id } = useParams()


    // useEffect((props) => {
    // props.fetchPostDetail(id)
    // }, [])

    const handleChange = async (e) => {

       await props.UpdatePost({...post, [e.target.name]: e.target.value})
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        props.UploadPost(id, props.postState.post)
        //console.log(props.postState.post)

    }

    return(
        <div className="create-post">
             {/* {props.postState.post && (  */}
            <div>
                {/* {props.postState.posts.map((post) => (
                    <AddPost 
                        handleSubmit={handleSubmit}
                        handleChange={handleChange}
                        />
                ))} */}
            <ReactStars
                onClick={null}
                size={24}
                color2={'#ffd700'}
                className={'stars'}
                value={props.postState.post.rating}
                half={false}
            />
            <textarea
                onChange={handleChange}
                value={props.postState.post.image}
                placeholder="Add a Picture"
            />
            <textarea
                onChange={handleChange}
                value={props.postState.post.recipeUrl}
                placeholder="Include a Link"
            /> 
            </div>


            <button onClick={handleSubmit}>
                {props.postState.post ? `Send` : `Create a Post`}
            </button>
        </div>
    )
}

export default connect(mapStateToProps, mapDispatchToProps)(CreatePost)
