import React, {useState, useEffect} from 'react'
import { connect } from 'react-redux'
import {
  UploadPost,
  UploadComment,
  UpdateComment,
  ToggleMoreComment
} from '../store/actions/PostDetailActions'
import { useParams } from 'react-router-dom'
import ReactStars from 'react-stars'


const mapStateToProps = ({ postDetailState }) => {
    return { postDetailState }
  }

const mapDispatchToProps = (dispatch) => {
    return {
      fetchPostDetail: (id, newPost) => dispatch(UploadPost(id, newPost)),
      uploadComment: (id, newComment) => dispatch(UploadComment(id, newComment)),
      updateComment: (comment) => dispatch(UpdateComment(comment)),
      toggleMoreComment: (value) => dispatch(ToggleMoreComment(value))
    }
}

const CreatePost = () => {
    const { id } = useParams()


    const [newPost, setNewPost] = useState({
        image: '',
        url: '',
        rating: 0
    })


    useEffect((props) => {
    props.fetchPostDetail(id)
    }, [id])
    const handleChange = async (e) => {
        await setNewPost(e.target.value)
    }

    return(
        <div className="create-post">
            <ReactStars
                onChange={''}
                size={24}
                color2={'#ffd700'}
                className={'stars'}
            />
            <textarea
                onChange={handleChange}
                value={newPost.image}
                placeholder="Add a Picture"
            />
            <textarea
                onChange={handleChange}
                value={newPost.url}
                placeholder="Include a Link"
            />
        </div>
    )
}

export default connect(mapStateToProps, mapDispatchToProps)(CreatePost)
