import React, {useState, useEffect} from 'react'
//import { connect } from 'react-redux'
//import {LoadPostDetail, UploadPost, UpdatePost} from '../store/actions/PostActions'
import { useParams } from 'react-router-dom'
import ReactStars from 'react-stars'
import {AddPost} from '../services/PostServices'


// const mapStateToProps = ({ postState }) => {
//     return { postState }
//   }

// const mapDispatchToProps = (dispatch) => {
//     return {
//       fetchPostDetail: (id) => dispatch(LoadPostDetail(id)),
//       UploadPost: (id, post) => dispatch(UploadPost(id, post)),
//       UpdatePost: (post) => dispatch(UpdatePost(post))
//     }
// }

const CreatePost = (props) => {
    const { id } = props.id
    const [post, setPost] = useState({
        image: '',
        description: '',
        recipeUrl: '',
        rating: 0
    })

    const handleChange = (e) => {
        setPost({...post, [e.target.name]: e.target.value}) 
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        AddPost(id, post) 
    }

    return(
        <div className="create-post">
             {/* {props.postState.post && (  */}
            <div>
                {/* {props.postState.posts.map((post) => (
                    <AddPost 
                        handleSubmit={handleSubmit}
                        handleChange={handleChange}
                        />
                ))} */}
            <ReactStars
                onClick={null}
                size={24}
                color2={'#ffd700'}
                className={'stars'}
                value={post.rating}
                name='rating'
                half={false}
            />
            <textarea
                onChange={handleChange}
                value={post.image}
                name='image'
                placeholder="Add a Picture"
            />
            <textarea
                onChange={handleChange}
                value={post.description}
                name="description"
                placeholder="Tell Us About It..."
            /> 
            <textarea
                onChange={handleChange}
                value={post.recipeUrl}
                name='recipeUrl'
                placeholder="Include a Link"
            /> 

            </div>


            <button onClick={handleSubmit}>
                {post ? `Send` : `Create a Post`}
            </button>

        </div>

    )
}
export default CreatePost
