const Profile = (props) => {

    const [user, setUser] = useState({})
    const [posts, setPosts] = useState([])



    useEffect(() => {
        // if(props.user){
        const getData = async () => {
            const data = await GetUserDetail(props.user.id)
            console.log(data)
            setUser(data)
        }
        getData()
        // console.log(data)

    }, [props.user])

    //console.log(user.id)

    return(
        <div className='user-profile'>
            {props.user.map((u) => (
            <div className='profile-banner'>
            <h1> Profile Page </h1>
            <h2>{u.fullname}</h2>
            </div>
            ))}
        </div>
    )
}
export default Profile
